var busca_estado = "";
var busca_cidade = "";

function buscar() {
  estado = document.getElementsByName("estado")[0];
  busca_estado = estado[estado.selectedIndex].value;
  busca_cidade = document.getElementsByName("cidade")[0].value;
  document.getElementById("depoimentos").innerHTML = "";
  depoimentos();
} // func

function depoimento( email, nome, cidade, data, depoimento, img, idproduto ) {
  var o;
  estado = cidade.split("-")[1];
  cidade = cidade.split("-")[0]
  o = "<div id=depoimento><table><td width=400><b>";
  if( email.length > 0 ) o += "<a href=mailto:"+email+">";
  o += nome;
  if( email.length > 0 ) o += "</a>";
  o += " - "+cidade+"-"+estado;
  o += " ("+data+")</b>\n";
  o += "<p>"+depoimento+"\n";
  var isarr = ( typeof img == "object" );
  if( img && ! isarr ) img = [img];
  if( img ) for( i=0; i<img.length; i++ ) {
    if( isarr ) a_idproduto = idproduto[i];
    else a_idproduto = idproduto;
    if( isarr ) a_img = img[i];
    else a_img = img;
    o += "\n<td nowrap>";
    if( idproduto ) if( a_idproduto ) o += "<a href=ListaProdutos.asp?IDLoja="+idloja+"&IDProduto="+a_idproduto+">";
    o += "<img src="+prodloja+a_img+" style='max-width:120px; max-height:120px;'>";
    if( idproduto ) if( a_idproduto ) o += "</a>";
  } // for
  o += "\n</table></div>";
  if( busca_estado && estado != busca_estado ) return false;
  if( busca_cidade ) var exp = new RegExp( busca_cidade, "i" );
  if( busca_cidade && cidade.search(exp) < 0 ) return false;
  document.getElementById("depoimentos").innerHTML += o;
} // func